// Macro

#define vblflip() { spcProcess();    WaitForVBlank(); }

/*!\brief Initialize the Rare logo screen.
*/
void initRareLogo() {
    setBrightness(0); 
    dmaClearVram(); 

    logoState = 0;
    framesCounter = 0;

    // Read tiles & map to VRAM  (interlace for mode 7)
    bgInitMapTileSet7(&logoPic, &logoMap, &logoPalette, (&logoPic_end - &logoPic), 0x0000);

    // Now Put mode7 without anything else
    setMode7(0);

    bgSetScroll(0, 0, 0);

    // Set mode 7 map scale
    logoScale = 256;
    REG_M7A = logoScale;
    REG_M7A = logoScale>>8;
    REG_M7D = logoScale;
    REG_M7D = logoScale>>8;

    // Set mode 7 map {x,y} position
    sx = 261;
    sy = 215;
    REG_M7X = (sx) & 255;
    REG_M7X = (sx) >> 8;
    REG_M7Y = (sy) & 255;
    REG_M7Y = (sy) >> 8;

    setBrightness(0xF);
}

#define REG_MISC (*(vuint8 *)0x2133)

void initRareLogoMode1() {
    setBrightness(0); 

    bgSetMapPtr(BG0, 0x7400, SC_32x32);
    bgInitTileSet(BG0, 
        &mode5Pic, 
        &mode5Palette, 
        PAL0, 
        (&mode5Pic_end - &mode5Pic), 
        32 * 7, 
        BG_16COLORS, 
        0x4000);
    WaitForVBlank();
    dmaCopyVram((u8 *)logoTileMap, 0x7400, 32*32*2);
    
    setMode(BG_MODE1, BG3_MODE1_PRORITY_HIGH);
    bgSetEnable(BG0);
    bgSetDisable(BG1);
    bgSetEnable(BG2);
    bgSetDisable(BG3);

    setBrightness(0xF);
}

void initRareLogoMode5() {
    setBrightness(0); 

    bgSetMapPtr(BG0, 0x7400, SC_32x32);
    bgInitTileSet(BG0, 
        &mode5Pic, 
        &mode5Palette, 
        PAL0, 
        (&mode5Pic_end - &mode5Pic), 
        32 * 7, 
        BG_16COLORS, 
        0x4000);
    WaitForVBlank();
    dmaCopyVram((u8 *)logoTileMap, 0x7400, 32*32*2);

    REG_BGMODE = BG_MODE5;
	REG_TM = BG1_ENABLE | BG2_ENABLE | OBJ_ENABLE;
	REG_TS = BG1_ENABLE | BG2_ENABLE | OBJ_ENABLE;
	REG_NMITIMEN = INT_VBLENABLE | INT_JOYPAD_ENABLE; 
    
    bgSetEnable(BG0);
    bgSetDisable(BG1);
    bgSetEnable(BG2);
    bgSetDisable(BG3);

    setBrightness(0xF);
}

/*!\brief Update Rare logo animation.
    \return 1 when the logo animation is complete, 0 otherwise.
*/
u8 updateRareLogo() {
    switch(logoState) {
        case 0:
            if (framesCounter == 32) {
                logoState = 1;
            }
            break;

        case 1:
            if (logoScale < 880) {
                logoScale += 8;

                REG_M7A = logoScale; // Set the value in 1st byte
                REG_M7A = logoScale>>8; // Set the value in 2nd byte

                REG_M7D = logoScale; // Set the value in 1st byte
                REG_M7D = logoScale>>8; // Set the value in 2nd byte
            }
            break;
    }

    framesCounter++;

    vblflip();

    return 0;
}
